pragma solidity ^0.8.0;

contract IdentityRegistry {
    struct Identity {
        address onchainID;
        uint256 nationality;
        uint256 country;
    }

    mapping(address => Identity) public identities;

    function addIdentity(address _user, address _onchainID, uint256 _nationality, uint256 _country) public {
        identities[_user] = Identity(_onchainID, _nationality, _country);
    }

    function getIdentity(address _user) public view returns (Identity memory) {
        return identities[_user];
    }
}","keccak256":"0x3c94fb3d5f3668393c0c62a078bf7c13a019360fda684a9a6dc283d2c9dc2160","license":"MIT"},"contracts/ModularCompliance.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./IdentityRegistry.sol";

contract ModularCompliance {
    IdentityRegistry identityRegistry;

    constructor(address _identityRegistry) {
        identityRegistry = IdentityRegistry(_identityRegistry);
    }

    function isCompliant(address _user, uint256 _requiredNationality, uint256 _requiredCountry) public view returns (bool) {
        IdentityRegistry.Identity memory identity = identityRegistry.getIdentity(_user);
        return identity.nationality == _requiredNationality && identity.country == _requiredCountry;
    }
}","keccak256":"0x8c6177067422cea7e7c8a1841393b438e4ce11cf5c0a1021b952ca3b0ec5865d","license":"MIT"},"contracts/Roles.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/AccessControl.sol";

contract Roles is AccessControl {
    bytes32 public constant MASTER_ROLE = keccak256("MASTER_ROLE");
    bytes32 public constant CUSTODIAN_ROLE = keccak256("CUSTODIAN_ROLE");
    bytes32 public constant ISSUER_ROLE = keccak256("ISSUER_ROLE");
    bytes32 public constant INVESTOR_ROLE = keccak256("INVESTOR_ROLE");
    bytes32 public constant AUDITOR_ROLE = keccak256("AUDITOR_ROLE");
    bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");
    bytes32 public constant CHARIAA_COMPLIANCE_ROLE = keccak256("CHARIAA_COMPLIANCE_ROLE");

    constructor() {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(MASTER_ROLE, msg.sender);
    }

    function addRole(address account, bytes32 role) public onlyRole(MASTER_ROLE) {
        grantRole(role, account);
    }

    function removeRole(address account, bytes32 role) public onlyRole(MASTER_ROLE) {
        revokeRole(role, account);
    }
}","keccak256":"0x8ff124c41f532390ecfa10617af2175cee3063945eda8d7e554dc37a8687a410","license":"MIT"},"contracts/SukukBond.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "./Roles.sol";
import "./ModularCompliance.sol";

contract SukukBond is ERC1155, Ownable, AccessControl {
    struct BondDetails {
        string name;
        string symbol;
        uint256 totalSupply;
        uint256 couponPeriod;
        uint256 maturity;
        string currency;
        string esgLabel;
        string category;
        address onchainID;
    }

    bytes32 public constant ISSUER_ROLE = keccak256("ISSUER_ROLE");
    bytes32 public constant MASTER_ROLE = keccak256("MASTER_ROLE");

    mapping(uint256 => BondDetails) public bondDetails;
    ModularCompliance public compliance;

    event UpdatedTokenInformation(
        uint256 indexed id,
        string name,
        string symbol,
        uint256 totalSupply,
        uint256 couponPeriod,
        uint256 maturity,
        string currency,
        string esgLabel,
        string category,
        address onchainID
    );
    event IdentityRegistryAdded(address indexed identityRegistry);
    event ComplianceAdded(address indexed compliance);

    constructor(string memory uri, address _compliance) ERC1155(uri) {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(ISSUER_ROLE, msg.sender);
        _setupRole(MASTER_ROLE, msg.sender);
        compliance = ModularCompliance(_compliance);
        emit ComplianceAdded(_compliance);
    }

    function createBond(
        uint256 id,
        string memory name,
        string memory symbol,
        uint256 totalSupply,
        uint256 couponPeriod,
        uint256 maturity,
        string memory currency,
        string memory esgLabel,
        string memory category,
        address onchainID,
        bytes memory data
    ) public onlyRole(ISSUER_ROLE) {
        bondDetails[id] = BondDetails(
            name,
            symbol,
            totalSupply,
            couponPeriod,
            maturity,
            currency,
            esgLabel,
            category,
            onchainID
        );
        _mint(msg.sender, id, totalSupply, data);
        emit UpdatedTokenInformation(
            id,
            name,
            symbol,
            totalSupply,
            couponPeriod,
            maturity,
            currency,
            esgLabel,
            category,
            onchainID
        );
    }

    function getBondDetails(uint256 id) public view returns (BondDetails memory) {
        return bondDetails[id];
    }

    function addAttributes(uint256 id, string memory newCategory) public onlyRole(MASTER_ROLE) {
        bondDetails[id].category = newCategory;
    }

    function transferBond(
        address to,
        uint256 id,
        uint256 amount,
        uint256 requiredNationality,
        uint256 requiredCountry
    ) public {
        require(compliance.isCompliant(to, requiredNationality, requiredCountry), "Compliance check failed");
        safeTransferFrom(msg.sender, to, id, amount, "");
    }

    function setURI(string memory newuri) public onlyOwner {
        _setURI(newuri);
    }

    // Override supportsInterface
    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC1155, AccessControl) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}","keccak256":"0xeaaeef6c9c1c4082b61835205d38ffc1468acf339ed1fb424e0d896017ed2a3b","license":"MIT"},"contracts/TREXFactory.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./SukukBond.sol";

contract TREXFactory {
    event SukukBondCreated(address sukukBondAddress);

    function createSukukBond(string memory uri, address compliance) public returns (address) {
        SukukBond sukukBond = new SukukBond(uri, compliance);
        emit SukukBondCreated(address(sukukBond));
        return address(sukukBond);
    }
}","keccak256":"0xdc51b56458b9e57be864a5a413d69f494eccd0d96881682e5121bafeb88125e0","license":"MIT"}},"version":1}
